import React, { useState } from 'react';
import './Contact.css';
import { FaInstagram, FaLinkedin, FaGithub } from 'react-icons/fa';

const Contact = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    mensagem: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Dados enviados:', formData);
    alert('Obrigado por entrar em contato! üòÄ');
  };

  return (
    <section id="contact">
      <div className="contact-container">
        <h1>Entre em Contato</h1>
        <p>Quer conversar ou colaborar comigo? Sinta-se √† vontade para me enviar uma mensagem! ‚úâÔ∏è</p>

        {/* Links para redes sociais */}
        <div className="social-links">
          <a href="https://www.instagram.com/seu_instagram" target="_blank" rel="noopener noreferrer">
            <FaInstagram /> Instagram
          </a>
          <a href="https://www.linkedin.com/in/seu_linkedin" target="_blank" rel="noopener noreferrer">
            <FaLinkedin /> LinkedIn
          </a>
          <a href="https://github.com/seu_github" target="_blank" rel="noopener noreferrer">
            <FaGithub /> GitHub
          </a>
        </div>

        {/* Formul√°rio de contato */}
        <form onSubmit={handleSubmit} className="contact-form">
          <input
            type="text"
            name="nome"
            placeholder="Seu Nome"
            value={formData.nome}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Seu E-mail"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <textarea
            name="mensagem"
            placeholder="Sua Mensagem"
            rows="5"
            value={formData.mensagem}
            onChange={handleChange}
            required
          />
          <button type="submit">Enviar</button>
        </form>
      </div>
    </section>
  );
};

export default Contact;
