import React, { useRef } from 'react';
import './Projects.css';
import Game1 from '../../assets/videos/Game1.mp4';
import Romance from '../../assets/videos/Romance.mp4';
import Projeto3 from '../../assets/videos/QuemEuSou.mp4';

const projectsData = [
  {
    id: 1,
    video: Game1,
    title: "Os lendários-Adventure",
    description: "Projeto do TCC onde criamos uma plataforma para ajudar estudantes com materiais interativos.",
    github: "https://github.com/tehzinha/jogoPrincipal"
  },
  {
    id: 2,
    video: Projeto2,
    title: "Jogo de história-Drama",
    description: "Jogo de aventura em 2D com mecânicas de plataforma e desafios progressivos.",
    github: "https://github.com/seu_github/Romance"
  },
  {
    id: 3,
    video: Projeto3,
    title: "E-commerce de Tecnologia",
    description: "Marketplace voltado para produtos de tecnologia, com carrinho de compras e pagamentos.",
    github: "https://github.com/seu_github/ecommerce-tecnologia"
  }
];

const Projects = () => {
  const scrollRef = useRef(null);

  const handlePlayPause = (event) => {
    const video = event.target;
    if (video.paused) {
      video.play();
    } else {
      video.pause();
    }
  };

  return (
    <section id="projects">
      <div className="projects-container">
        <h1>Meus Projetos</h1>
        <p className="projects-warning">
          🚧 Alguns jogos ainda estão em manutenção, por isso não estão jogáveis no momento. 🚀
        </p>

        <div className="projects-scroll" ref={scrollRef}>
          {projectsData.map((project) => (
            <div key={project.id} className="project-card">
              <video 
                src={project.video} 
                className="project-video"
                onClick={handlePlayPause} 
                muted 
                loop 
                playsInline
              />
              <div className="project-info">
                <h2>{project.title}</h2>
                <p>{project.description}</p>
                <a href={project.github} target="_blank" rel="noopener noreferrer">
                  🔗 Ver no GitHub
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
