import React, { useState, useRef } from "react";
import "./Projects.css";
import { FaPlay } from "react-icons/fa";
import Game1 from "../../assets/videos/Game1.mp4";
import Romance from "../../assets/videos/Romance.mp4";
import Game2 from "../../assets/videos/Game2.mp4";

const projectsData = [
  {
    id: 1,
    video: Game1,
    title: "Os lendÃ¡rios-Adventure",
    description: "Um jogo de aventura, onde os herÃ³is que uma vez jÃ¡ eram adorados por todos, foram esquecidos pela nova geraÃ§Ã£o. Mas um novo perigo se aproxima e eles sentem que Ã© hora de voltar! Foi utilizado as linguag",
    github: "https://github.com/tehzinha/jogoPrincipal",
  },
  {
    id: 2,
    video: Romance,
    title: "Jogo de histÃ³ria-Drama",
    description: "Jogo de aventura em 2D com mecÃ¢nicas de plataforma e desafios progressivos.",
    github: "https://github.com/seu_github/jogoInteracao",
  },
  {
    id: 3,
    video: Game2,
    title: "Monkey Game-Adventure",
    description: "Marketplace voltado para produtos de tecnologia, com carrinho de compras e pagamentos.",
    github: "https://github.com/seu_github/ecommerce-tecnologia",
  },
];

const Projects = () => {
  const scrollRef = useRef(null);
  const [playing, setPlaying] = useState({});

  const handlePlayPause = (id, event) => {
    const video = event.target;
    if (video.paused) {
      video.play();
      setPlaying((prev) => ({ ...prev, [id]: true }));
    } else {
      video.pause();
      setPlaying((prev) => ({ ...prev, [id]: false }));
    }
  };

  return (
    <section id="projects">
      <div className="projects-container">
        <h1>Meus Projetos</h1>
        <p className="projects-warning">
          ðŸš§ Alguns jogos ainda estÃ£o em manutenÃ§Ã£o, por isso nÃ£o estÃ£o jogÃ¡veis no momento. ðŸš€
        </p>

        <div className="projects-scroll" ref={scrollRef}>
          {projectsData.map((project) => (
            <div key={project.id} className="project-card">
              <div className="video-containerr" onClick={(e) => handlePlayPause(project.id, e)}>
                {!playing[project.id] && <div className="play-icon"><FaPlay /></div>}
                <video
                  src={project.video}
                  className="project-video"
                  muted
                  loop
                  playsInline
                />
              </div>
              <div className="project-info">
                <h2>{project.title}</h2>
                <p>{project.description}</p>
                <a href={project.github} target="_blank" rel="noopener noreferrer">
                  ðŸ”— Ver no GitHub
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
